    }

    render = () => {
        return (
            <main className='container'>
                <h1 className="timer">{this.getTime()}</h1>
                <div className='buttons'>
                    <button>Start</button>
                    <button>Stop</button>
                    <button onClick={this.reset}>Reset</button>
                </div>
            </main>
        );
    }
}
